function showsubMenu(mainMenuName,subMenuName,callback){ 
	var index = getIndex(mainMenuName);
	var submenu= ["",subMenuName,callback,null,""];
	if(index==-1)
	{
		var firstLevelMenu= ["",mainMenuName,"",null,"",submenu]; 
		 myMenu.insertAt(myMenu.length,firstLevelMenu); 	
	}
	else{
		myMenu[index].insertAt(myMenu[index].length,submenu);



	}
}




function setPos(e)
{
	try
	{
		curevent=(typeof event=='undefined'?e:event);
		mouseStatus='down';
		curPos=curevent.clientY;
		FrmHeight=document.getElementById('IdDivFrm').style.height;
		DocHeight=document.getElementById('docDiv').style.height; 
		InitialPos=curPos;

	}
	catch(e)
	{
		alert(e.message);
	}
}
function getPos(e)
{
	try
	{
		if(mouseStatus=='down'){ 
			curevent=(typeof event=='undefined'?e:event);
			mouseStatus='down';
			curPos=curevent.clientY;
			CordPos=parseInt(InitialPos)-parseInt(curPos);

			document.getElementById('docDiv').style.height=parseInt(DocHeight)-parseInt(CordPos); 
			document.getElementById('IdDivFrm').style.height=parseInt(FrmHeight);
			DocHeight=document.getElementById('docDiv').style.height; //Bhupesh 
			FrmHeight=document.getElementById('IdDivFrm').style.height; //Bhupesh 
		}
		mouseStatus='up';
	}
	catch(e)
	{
		alert(e.message);
	}
}


function ShowForm(e)
{


	getPos(e);

	try
	{
		if(document.getElementById("docDiv").style.display == 'inline')
		{
			document.getElementById("IdDivFrm").style.display="inline";

			document.getElementById("docDiv").style.display="none";
		}
		document.getElementById("IdImgForm").src="/webdesktop/webtop/en_us/images/tab_form_en.gif";
		document.getElementById("IdImgDoc").src="/webdesktop/webtop/en_us/images/tab_doc_dis.gif";
		var h = document.body.offsetHeight;
alert(h);
        document.getElementById("IdDivFrm").style.height = (h - 25) + "px";
alert( document.getElementById("IdDivFrm").style.height);
	}
	catch(e)
	{
		alert(e.message);
	}
	return false;
}


