function ShowDoc()
{
	try
	{
		if(document.getElementById("IdDivFrm").style.display == 'inline' || document.getElementById("IdDivFrm").style.display == ''|| document.getElementById("IdDivFrm").style.display == 'block')


		{
			document.getElementById("IdDivFrm").style.display="none";
			document.getElementById("docDiv").style.display="inline";
		}

		document.getElementById("IdImgForm").src="/webdesktop/webtop/en_us/images/tab_form_dis.gif";
		document.getElementById("IdImgDoc").src="/webdesktop/webtop/en_us/images/tab_doc_en.gif";
	}
	catch(e)
	{
		alert(e.message);
	}
	return false;
}


